package com.leetcode.mytest;

public class PermutationInString1 {
	public static void main(String[] args) {
		PermutationInString1 pis = new PermutationInString1();
		 System.out.println(pis.checkInclusion("adc", "dcda"));
		 System.out.println(pis.checkInclusion("ab", "eidbaooo"));
		 System.out.println(pis.checkInclusion("ab", "eidboaooo"));
		StringBuilder b = new StringBuilder();
		b.append("");
		b.append("");
		b.append("");
		b.append("");
		b.append("");
		b.append("");
		b.append("");
		System.out.println(pis.checkInclusion("xuumbjffxuzovdwrnolopeingppzgorotzdqfprokkmucxwsub",
		                                      b.toString()));
	}

	public boolean checkInclusion(String s1, String s2) {
		if (s1 == null || s2 == null || s1.length() > s2.length()) {
			return false;
		}
		if (s1.isEmpty() || s2.contains(s1)) {
			return true;
		}
		return canConsist(s1, s2);
	}

	private boolean canConsist(String s1, String s2) {
		char[] s1Arr = new char[26];
		char[] origin = new char[26];
		int total = 0;
		for (int i = 0; i < s1.length(); i++) {
			char c = s1.charAt(i);
			s1Arr[c - 'a'] += 1;
			origin[c - 'a'] += 1;
			total++;
		}
		int count = 0;
		int start = 0;
		for (int i = 0; i < s2.length(); ) {
			char c = s2.charAt(i);
			if (s1Arr[c - 'a'] > 0) {
				s1Arr[c - 'a'] -= 1;
				count++;
				if (total == count) {
					return true;
				}
				i++;
			}
			else {
				reset(s1Arr, origin);
				count = 0;
				i = start++;
			}
		}
		return false;
	}

	private void reset(char[] s1Arr, char[] origin) {
		int n = origin.length;
		for (int i = 0; i < n; i++) {
			s1Arr[i] = origin[i];
		}

	}

}
